function f1_createMondayDraftWithDate() {
  // Get the current date
  var today = new Date();

  // Format the date (example: "03/10/2025")
  var formattedDate = Utilities.formatDate(today, Session.getScriptTimeZone(), "MM/dd/yyyy");

  // Set email parameters
  var recipient = "reportmail.sample@sample.com"; // Change this to your recipient email
  var subject = "TechName, " + formattedDate + ", Address_1, Dr. A";  // Subject includes the date

  // Use HTML formatting to make "Best regards" italic and move it two rows down
  var body = "<br><br>--<br><i>Best regards</i>"; // Two line breaks before "Best regards"

  // Create draft in Gmail with HTML body
  GmailApp.createDraft(recipient, subject, "", {
    htmlBody: body
  });

  // Log the draft creation
  Logger.log("Draft created for: " + recipient + " with subject: " + subject);
}

