function f3_createWed1and3DraftWithDate() {
  var today = new Date();

  // Check if today is Wednesday (3 = Wednesday in JavaScript's Date object)
  if (today.getDay() !== 3) {
    Logger.log("Not a Wednesday. Script stopped.");
    return;
  }

  // Get the current month's Wednesdays
  var firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
  var wednesdays = [];

  // Find all Wednesdays in the month
  for (var day = 1; day <= 31; day++) {
    var date = new Date(today.getFullYear(), today.getMonth(), day);
    if (date.getMonth() !== today.getMonth()) break; // Stop at the end of the month
    if (date.getDay() === 3) wednesdays.push(date);
  }

  // Check if today is the 1st or 3rd Wednesday
  if (!(today.toDateString() === wednesdays[0].toDateString() || today.toDateString() === wednesdays[2].toDateString())) {
    Logger.log("Not the 1st or 3rd Wednesday. Script stopped.");
    return;
  }

  // Format the date for the email subject
  var formattedDate = Utilities.formatDate(today, Session.getScriptTimeZone(), "MM/dd/yyyy");

  // Set email parameters
  var recipient = "reportmail.sample@sample.com"; 
  var subject = "TechName, " + formattedDate + ", Address_3, Dr. C";

  // Use HTML formatting to make "Best regards" italic and move it two rows down
  var body = "<br><br>--<br><i>Best regards</i>";

  // Create a Gmail draft with the formatted subject and body
  GmailApp.createDraft(recipient, subject, "", {
    htmlBody: body
  });

  Logger.log("Draft created for: " + recipient + " with subject: " + subject);
}

function f4_createWed2and4DraftWithDate() {
  var today = new Date();

  // Check if today is Wednesday (3 = Wednesday in JavaScript's Date object)
  if (today.getDay() !== 3) {
    Logger.log("Not a Wednesday. Script stopped.");
    return;
  }

  // Get the Wednesdays of the current month
  var firstDayOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
  var wednesdays = [];

  // Find all Wednesdays in the month
  for (var day = 1; day <= 31; day++) {
    var date = new Date(today.getFullYear(), today.getMonth(), day);
    if (date.getMonth() !== today.getMonth()) break; // Stop at the end of the month
    if (date.getDay() === 3) wednesdays.push(date);
  }

  // Check if today is the 2nd or 4th Wednesday
  if (!(today.toDateString() === wednesdays[1].toDateString() || today.toDateString() === wednesdays[3].toDateString())) {
    Logger.log("Not the 2nd or 4th Wednesday. Script stopped.");
    return;
  }

  // Format the date for the email subject
  var formattedDate = Utilities.formatDate(today, Session.getScriptTimeZone(), "MM/dd/yyyy");

  // Set email parameters
