function f6_createBiweeklyFridayDraft() {
  var today = new Date();

  // Check if today is Friday (5 = Friday in JavaScript's Date object)
  if (today.getDay() !== 5) {
    Logger.log("Not a Friday. Script stopped.");
    return;
  }

  // Define the start date (03/21/2025)
  var startDate = new Date(2025, 2, 21); // March is month index 2 (0-based index)

  // Calculate the difference in days between today and the start date
  var diffInDays = Math.floor((today - startDate) / (1000 * 60 * 60 * 24));

  // Check if today is exactly a multiple of 14 days from the start date
  if (diffInDays % 14 !== 0) {
    Logger.log("Not a scheduled biweekly Friday. Script stopped.");
    return;
  }

  // Format the date for the email subject
  var formattedDate = Utilities.formatDate(today, Session.getScriptTimeZone(), "MM/dd/yyyy");

  // Set email parameters
  var recipient = "reportmail.sample@sample.com"; 
  var subject = "TechName, " + formattedDate + ", Address_2, Dr. B";

  // Use HTML formatting to make "Best regards" italic and move it two rows down
  var body = "<br><br>--<br><i>Best regards</i>"; 

  // Create a Gmail draft with the formatted subject and body
  GmailApp.createDraft(recipient, subject, "", {
    htmlBody: body
  });

  Logger.log("Draft created for: " + recipient + " with subject: " + subject);
}
